import React, { useState, useEffect } from 'react'
import NavbarUser from '../../layouts/NavbarUser'
import NavUserDashboard from '../../layouts/NavUserDashboard';
import { Link, useNavigate } from "react-router-dom";

// redux
import { useSelector } from 'react-redux'

// function
import { readUsers } from '../../functions/user'

const UserProfileEdit = () => {
	const { user } = useSelector((state) => ({ ...state }))
	const [userdata, setUserdata] = useState([])
	console.log(user.id)
	useEffect(() => {
		loadData(user.token, user.id)
	}, [])

	const loadData = (authtoken, id) => {
		readUsers(authtoken, id)
			.then((res) => {
				setUserdata(res.data)
			})
			.catch((err) => {
				console.log(err)
			})
	}

	const handleChangeDorm = (e) => {
    setDorm({
      ...dorm,
      [e.target.name]: e.target.value,
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    editDorm(user.token, param.id, dorm)
      .then((res) => {
        console.log(res)
      })
      .catch((err) => {
        console.log(err)
      })
  }

	return (
		<div>
			<NavbarUser />
			<div className='container py-5'>
				<div class="row">
					<div class="d-flex justify-content-between align-items-center experience">
						<h3>เเก้ไขข้อมูลส่วนตัว</h3>
						<Link class="btn btn-outline-secondary" to='/user/dashboard'>ย้อนกลับ</Link>
					</div><br />
				</div><hr />
				<form onSubmit={handleSubmit}>
          <div className="form-group">
            <div className="col-md-6">
              <label className="col-form-label">ชื่อหอพัก</label>
              <input type="text" className="form-control" name="name" placeholder="ชื่อหอพัก" value={dorm.name} required onChange={handleChangeDorm} />
            </div>
            <button className="mt-4 col-md-6 btn btn-outline-primary profile-button">เเก้ไขข้อมูลหอพัก</button>
          </div>
        </form><hr />
			</div>
		</div>
	)
}

export default UserProfileEdit
