import React, { useState } from 'react'

import { login } from '../../functions/auth'

// redux
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'

const Login = () => {
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const [value, setValue] = useState({
    username: "",
    password: "",
  })

  const roleBaseRedirect = (role) => {
    if (role === 'admin') {
      navigate('/admin/index');
    } else {
      navigate('/user/index');
    }
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value,
    })
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(value)
    //code
    login(value)
      .then(res => {
        console.log(res.data)
        alert(res.data)
        dispatch({
          type: 'LOGIN',
          payload: {
            token: res.data.token,
            username: res.data.payload.user.username,
            role: res.data.payload.user.role,
          }
        });
        localStorage.setItem('token', res.data.token)
        roleBaseRedirect(res.data.payload.user.role)
      }).catch(err => {
        console.log(err.response.data)
        alert(err.response.data)
      })

  }
  return (
    <div class="row bg-white">
      <div class="col-md-8 border-right">
        <img width="100%" height="100%" src="https://media.architecturaldigest.com/photos/5b72eaf7e4ca455528fdfa43/master/w_2048,h_1387,c_limit/Studio%20Gang_Campus%20North%20Residential%20Commons_Landscaped%20Commons_by%20Steve%20Hall%20Copyright%20Hedrich%20Blessing.jpg" />
      </div>
      <div class="col-md-4">
        <div class="text-center">
          <h4>หน้าเข้าสู่ระบบจองหอพัก</h4><hr />
        </div>
        
      </div>
    </div>
  )
}

export default Login
