import React, { useState, useEffect } from 'react'
import NavbarAdmin from '../../layouts/NavbarAdmin'

// function
import { createDorm } from '../../functions/dorm'
import { ThemeConsumer } from 'styled-components'

const AdminCreateDorm = () => {
  const [values, setValues] = useState({
    name: "",
  })

  const handleChangeDorm = (e) => {
    setValues({
      ...values,
      [e.target.name]: e.target.value,
    })
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    createDorm(values)
    .then((res)=>{
      console.log(res)
    }).catch((err)=>{
      console.log(err)
    })
  }

  return (
    <div>
      <NavbarAdmin />
      <div className='container'>
        <h3>เพิ่มหอพัก</h3><hr />
        <form onSubmit={handleSubmit}>
          <div className="col-md-6">
            <label className="col-form-label">ชื่อหอพัก</label>
            <input type="text" className="form-control" name="name" value={values.name} placeholder="ชื่อหอพัก" onChange={handleChangeDorm} />
          </div>
          <button className="mt-4 col-md-6 btn btn-outline-primary profile-button">เพิ่มหอพัก</button>
        </form>
      </div>
    </div>
  )
}

export default AdminCreateDorm
