import React, { useState, useEffect } from 'react'
import { Link, useNavigate } from "react-router-dom";

// redux
import { useSelector } from 'react-redux'

// function
import { readUsers } from '../functions/user'

const NavUserDashboard = () => {
	const { user } = useSelector((state) => ({ ...state }))
	const [userdata, setUserdata] = useState([])
	console.log(user.id)
	useEffect(() => {
		loadData(user.token, user.id)
	}, [])

	const loadData = (authtoken, id) => {
		readUsers(authtoken, id)
			.then((res) => {
				setUserdata(res.data)
			})
			.catch((err) => {
				console.log(err)
			})
	}
	return (
		<div className='col-lg-4'>
			<div class="card mb-4">
				<div class="card-body text-center">
					<img src="https://cdn.pixabay.com/photo/2015/10/05/22/37/blank-profile-picture-973460_1280.png" width='150px' alt="avatar" class="rounded-circle img-fluid" />
					<h5 class="my-3">{userdata.firstname} {userdata.lastname}</h5>
					<p class="text-muted mb-1">คณะ{userdata.faculty}</p>
					<p class="text-muted mb-4">สาขา{userdata.major}</p>
					<div class="d-flex justify-content-center mb-2">
						<Link type="button" class="btn btn-outline-primary ms-1">เเก้ไขโปรไฟล์</Link>
					</div>
				</div>
			</div>
			<div class="card mb-4 mb-lg-0">
				<div class="card-body p-0">
					<ul class="list-group list-group-flush rounded-3">
						<li class="list-group-item d-flex justify-content-between align-items-center p-3">
							<i class="fas fa-globe fa-lg text-warning"></i>
							<p class="mb-0">https://mdbootstrap.com</p>
						</li>
						<li class="list-group-item d-flex justify-content-between align-items-center p-3">
							<i class="fab fa-github fa-lg"></i>
							<p class="mb-0">mdbootstrap</p>
						</li>
					</ul>
				</div>
			</div>
		</div>
	)
}

export default NavUserDashboard
