import React, { useState, useEffect } from 'react'
import { Routes, Route } from 'react-router-dom'

import Register from "./components/pages/auth/Register";
import Login from "./components/pages/auth/Login";
import Home from './components/pages/Home';

// page admin
import AdminHome from './components/pages/admin/AdminHome';
import AdminManage from './components/pages/admin/AdminManage';

// page user
import UserHome from './components/pages/user/UserHome';


// functions
import { currentUser } from './components/functions/auth'
import { useDispatch } from 'react-redux'

// Routes
import UserRoute from './components/routes/UserRoute';
import AdminRoute from './components/routes/AdminRoute';

function App() {
  const dispatch = useDispatch()
  const idtoken = localStorage.token
  if (idtoken) {
    currentUser(idtoken)
      .then(res => {
        //code
        console.log(res.data)
        dispatch({
          type: 'LOGIN',
          payload: {
            token: idtoken,
            username: res.data.username,
            role: res.data.role,
          }
        });
      }).catch(err => {
        //err
        console.log(err)
      })
  }
  return (
    <Routes>
      <Route path='/' element={<Home />} />
      <Route path="/register" element={<Register />} />
      <Route path="/login" element={<Login />} />

      <Route path="/admin/index" element={
        <AdminRoute>
          <AdminHome />
        </AdminRoute>
      } />
      <Route path="/admin/manage-user" element={
        <AdminRoute>
          <AdminManage />
        </AdminRoute>
      } />
      <Route path="/user/index" element={
        <UserRoute>
          <UserHome />
        </UserRoute>
      } />
    </Routes>
  );
}

export default App;
