import React, { useState } from 'react'

import { login } from '../../functions/auth'

// redux
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'

const Login = () => {
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const [value, setValue] = useState({
    username: "",
    password: "",
  })

  const roleBaseRedirect = (role) => {
    if (role === 'admin') {
      navigate('/admin/index');
    } else {
      navigate('/user/index');
    }
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value,
    })
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(value)
    //code
    login(value)
      .then(res => {
        console.log(res.data)
        alert(res.data)
        dispatch({
          type: 'LOGIN',
          payload: {
            token: res.data.token,
            username: res.data.payload.user.username,
            role: res.data.payload.user.role,
          }
        });
        localStorage.setItem('token', res.data.token)
        roleBaseRedirect(res.data.payload.user.role)
      }).catch(err => {
        console.log(err.response.data)
        alert(err.response.data)
      })

  }
  return (
    <div class="bg-white">
      <div class="row">
        <div class="col-md-8 border-right">
          
        </div>
        <div class="col-md-4">
          <div class="col-md-10 text-center">
            <h4>หน้าเข้าสู่ระบบจองหอพัก</h4>
          </div>
          <form onSubmit={handleSubmit}>
            <div class="col-md-10">
              <label class="col-form-label">ชื่อผู้ใช้งาน (Username)</label>
              <input type="text" class="form-control" name="username" placeholder="Username" required onChange={handleChange}/>
            </div>
            <div class="col-md-10">
              <label class="col-form-label">รหัสผ่าน (Password)</label>
              <input type="password" class="form-control" name="password" placeholder="Password" required onChange={handleChange}/>
            </div>
            <button class="mt-5 col-md-10 btn btn-outline-primary profile-button">เข้าสู่ระบบ</button>
          </form>
        </div>
      </div>
    </div>
  )
}

export default Login
