import React, { useState } from 'react'

import { login } from '../../functions/auth'

// redux
import { useDispatch } from 'react-redux'
import { useNavigate } from 'react-router-dom'

const Login = () => {
  const navigate = useNavigate()
  const dispatch = useDispatch()

  const [value, setValue] = useState({
    username: "",
    password: "",
  })

  const roleBaseRedirect = (role) => {
    if (role === 'admin') {
      navigate('/admin/index');
    } else {
      navigate('/user/index');
    }
  }

  const handleChange = (e) => {
    setValue({
      ...value,
      [e.target.name]: e.target.value,
    })
  }
  const handleSubmit = (e) => {
    e.preventDefault()
    console.log(value)
    //code
    login(value)
      .then(res => {
        console.log(res.data)
        alert(res.data)
        dispatch({
          type: 'LOGIN',
          payload: {
            token: res.data.token,
            username: res.data.payload.user.username,
            role: res.data.payload.user.role,
          }
        });
        localStorage.setItem('token', res.data.token)
        roleBaseRedirect(res.data.payload.user.role)
      }).catch(err => {
        console.log(err.response.data)
        alert(err.response.data)
      })

  }
  return (
      <div class="row">
        <div class="col-md-8 border-right">
          <img width='100' height='100' src="https://www.collegemagazine.com/wp-content/uploads/2019/06/1200px-Escalante_Dorm_Room.jpg" />
        </div>
        <div class="col-md-4">
          <form onSubmit={handleSubmit}>
            <div class="row mt-2">
              <div class="col-md-6"><label class="col-form-label">ชื่อ (First name)</label><input type="text" class="form-control" placeholder="First name" name="firstname" value="" /></div>
              <div class="col-md-6"><label class="col-form-label">นามสกุล (Last name)</label><input type="text" class="form-control" placeholder="Last name" name="lastname" value="" /></div>
            </div>
            <label>Username</label>
            <input text="text" name="username" onChange={handleChange} /><br />
            <label>Password</label>
            <input text="text" name="password" onChange={handleChange} /><br />
            <button>Submit</button>
          </form>
        </div>
    </div>
  )
}

export default Login
