import React, { useState, useEffect } from 'react'
import NavbarUser from '../../layouts/NavbarUser'
import { Link, useParams, useNavigate } from "react-router-dom";

// function
import { listDormRoomID } from '../../functions/dormRoom'

// redux
import { useSelector } from 'react-redux';

const UserDormRoomDetail = () => {
	const navigate = useNavigate()
	const { user } = useSelector((state) => ({ ...state }))
	const param = useParams()
	const [room, setRoom] = useState([])

	useEffect(() => {
    loadData(user.token, param.id)
  }, [])

  const loadData = (authtoken, id) => {
    listDormRoomID(authtoken, id)
      .then((res) => {
        setDormroom(res.data)
      })
      .catch((err) => {
        console.log(err)
      })
  }
	console.log(dormroom)

	return (
		<div>
			<NavbarUser />
			<div className='container py-5'>
				<div className="row">
					<div className="d-flex justify-content-between align-items-center experience">
						<h3>รายละเอียดห้องพัก</h3>
						<button className="btn btn-outline-secondary" onClick={()=>navigate(-1)}>ย้อนกลับ</button>
					</div><br />
				</div><hr />
				<div className="row px-5">
					{dormroom.map((item)=>
						<Link className="card col-sm-3 mt-5 nav-link" key={item.id}>
							<div className="card-body">
								<h5 className="card-title">{item.dorm.name} ห้อง {item.roomID}</h5>
								<p className="card-text">ชั้น {item.floor} ห้องที่ {item.room}</p>
								<p className="card-text">ประเภทห้อง {item.roomtype}</p>
								<p className="card-text">ราคา {item.price} บาท</p>
							</div>
						</Link>
					)}
				</div>
			</div>
		</div>
	)
}

export default UserDormRoomDetail
